import React, { Component } from "react";
import {Row, Col, Container} from "react-bootstrap"
import Config from "../scripts/config.js"

class RobotState extends Component {
    state = {
        ros: null,
        x:0,
        y:0,
        orientation:0,
        linear_verlocity:0,
        angular_velocity:0,
    };

    constructor() {
        super();
        this.init_connection();
    }

    getRobotState(){
        // create pose subscriber
        var pose_subscriber = new window.ROSLIB.Topic({
                ros:this.state.ros,
                name: "/amcl_pose",
                messageType: "geometry_msgs/PoseWithCovarianceStamped",
        });

        // create pose callback
        pose_subscriber.subscribe((message) => {
            this.setState({x: message.pose.pose.position.x});
            this.setState({y: message.pose.pose.position.y});
        });


        // // create velocity subcriber
        // velocity_subscriber = new window.ROSLIB.Topic({
        //         ros:this.state.ros,
        //         name: "/odometry",
        //         messageType: "geometry_msgs/PoseWithCovarianceStamped",
        // });
    }

    init_connection(){
        this.state.ros = new window.ROSLIB.Ros(); // window must be used because package is called from index.html
        console.log(this.state.ros);

        this.state.ros.on("connection", () => {
            console.log("connection established in Teleop");
            this.setState({connected: true});
        }); 

        this.state.ros.on("close", () => {
            console.log("here");
            console.log("connection closed!");
            this.setState({connected: false});

            // try to reconnect every 3 seconds
            setTimeout(() => {
                try {
                    this.state.ros.connect(
                        "ws://" + 
                        Config.ROSBRIDGE_SERVER_IP + 
                        ":" +  
                        Config.ROSBRIDGE_SERVER_PORT +
                        ""
                    );
                } catch (error) {
                    console.log("connection problem");
                }
            }, Config.RECONNECTION_TIMER);
        });

        try {
            this.state.ros.connect(
                "ws://" + 
                Config.ROSBRIDGE_SERVER_IP + 
                ":" +  
                Config.ROSBRIDGE_SERVER_PORT +
                ""
            );
        } catch (error) {
            console.log("connection problem");
        }
    }

    componentDidMount(){
        this.getRobotState();
    }

    render() {
        return (
            <div>
                <Row>
                    <Col>
                        <h4 className="mt-4">Position</h4>
                        <p className="mt-0">X: {this.state.x}</p>
                        <p className="mt-0">Y: {this.state.y}</p>
                        <p className="mt-0">Orientation: {this.state.orientation}</p>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <h4 className="mt-4">Velocity</h4>
                        <p className="mt-0">Linear X: {this.state.linear_velocity}</p>
                        <p className="mt-0">Angular Z: {this.state.angular_velocity}</p>
                    </Col>
                </Row>
            </div>
        );
    }
}

export default RobotState;
