import React, { Component } from "react";
import Alert from "react-bootstrap/Alert"
import Config from "../scripts/config.js"


class Connection extends Component {
    state = {connected: false};

    constructor() {
        super();
        this.init_connection();
    }

    init_connection(){
        this.state.ros = new window.ROSLIB.Ros(); // window must be used because package is called from index.html
        console.log(this.state.ros);

        this.state.ros.on("connection", () => {
            console.log("connection established!");
            this.setState({connected: true})}); 

        this.state.ros.on("close", () => {
            console.log("connection closed");
            this.setState({connected: false})});
            // try to reconnect every 3 seconds
            setTimeout(()=>{
                try{
                    this.state.ros.connect("ws://"+Config.ROSBRIDGE_SERVER_IP+":9090");
                } catch (error) {
                    console.log("connection sproblem");
                }
            }, 3000);

    //     try{
    //         this.state.ros.connect("ws://172.16.51.88:9090");
    //     }catch (error) {console.log("connection sproblem")}
    }

    render() {
        return (
            <div>
                <Alert className="text-center m-3" variant={this.state.connected? "success": "danger"}>
                    {this.state.connected? "Robot Connected": "Robot Disconnected"}</Alert>
            </div>
        );

    }
}

export default Connection;
